@import 'variables';

.datepicker {
  display: block;
}

::ng-deep .datepicker {
  .p-inputtext {
    min-width: 265px;
  }

  .p-calendar:after {
    content: $icon-calendar;
    font-family: $font-family-icons;
    position: absolute;
    top: .5em;
    right: .5em;
    font-size: 1.3em;
    color: $primary-500;
    cursor: pointer;
    pointer-events: none;
  }

  .p-calendar .p-datepicker {
    top: calc(100% - 1px) !important;
    right: 0;
    font-size: $font-size-sm;
    padding-top: 0;

    &.p-datepicker-multiple-month {
      right: auto;
    }
  }

  .p-datepicker .p-datepicker-header {
    border-bottom: none;
    padding-bottom: 0;
    padding-top: 0;
    margin-bottom: -0.75em;
  }

  .p-link, .p-component, .p-datepicker table {
    font-size: inherit;
  }

  .p-datepicker {
    padding: 0;
  }

  .p-datepicker table th {
    text-align: center;
    font-weight: $font-weight-semibold;
  }

  .p-datepicker table th,
  .p-datepicker table td {
    padding: .25em .1em;

    > span {
      width: 2em;
      height: 2em;
    }
  }

  .p-datepicker table td.p-datepicker-today > span {
    background-color: $primary-200;
  }

  .p-datepicker table td > span.p-highlight,
  .p-datepicker:not(.p-disabled) table td span:not(.p-highlight):not(.p-disabled):hover {
    color: $white;
    background-color: $primary-600;
    font-weight: $font-weight-medium;
  }

  .p-datepicker table td.-highlighted,
  .p-datepicker table td.-highlighted-start,
  .p-datepicker table td.-highlighted-end {
    position: relative;

    &:before {
      content: '';
      position: absolute;
      top: .25em;
      left: 0;
      right: 0;
      bottom: .25em;
      background-color: $primary-250;
      z-index: -1;
    }
  }

  .p-datepicker table td.-highlighted-start:before {
    left: 50%;
  }

  .p-datepicker table td.-highlighted-end:before {
    right: 50%;
  }

  &.-showing {
    .p-inputtext {
      border-bottom-left-radius: 0;
      border-bottom-right-radius: 0;
      border-color: $primary-600;
      box-shadow: 0 0 0 1px $primary-600;
    }

    .p-inputtext + .p-datepicker {
      border-top-left-radius: 0;
      border-top-right-radius: 0;
      border-top: 2px solid $primary-600;
    }
  }

  .p-datepicker .p-datepicker-header .p-datepicker-title .p-datepicker-month {
    margin-right: 0;
  }

  .p-datepicker .p-datepicker-header .p-datepicker-prev,
  .p-datepicker .p-datepicker-header .p-datepicker-next {
    width: 1.5em;
    height: 1.5em;
  }

  .p-datepicker.-has-ranges.p-datepicker-multiple-month {
    display: inline-grid;
    grid-template-columns: 1fr 1fr;
    max-width: 100vw;
    overflow-x: auto;

    .p-datepicker-group {
      padding-right: .5em;
    }
  }
}

::ng-deep .datepicker__predefined-ranges {
  .p-menu {
    border: none;
    border-radius: 0;
    border-right: 1px solid $gray-300;
    background-color: $gray-100;
    font-size: $font-size-sm;
    width: auto;
    min-width: 150px;
    margin-right: .25em;
    height: 100%;
    padding-top: .5em;

    .p-menuitem-link {
      padding: 0.7em 1em;
      padding-right: 2em;
      color: $primary-600;
      font-weight: $font-weight-medium;

      .p-menuitem-text {
        color: inherit;
      }

      &.-active {
        background-color: $primary-250;
      }
    }
  }
}

.datepicker__actions {
  grid-column: span 3;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  padding: 1em;
  border-top: 1px solid $gray-300;
}

.datepicker__btn {
  margin-left: .25em;
  font-weight: $font-weight-medium;

  &.btn-link {
    text-decoration: none;

    &:hover {
      text-decoration: underline;
    }
  }

  &.-clear {
    margin-right: auto;
    margin-left: -.25em;
  }
}

